 <!doctype html>
 <html lang="en" xmlns="http://www.w3.org/1999/xhtml" xmlns:v="urn:schemas-microsoft-com:vml"
     xmlns:o="urn:schemas-microsoft-com:office:office">

 <head>
     <meta charset="UTF-8" />
     <meta http-equiv="x-ua-compatible" content="ie=edge" />
     <meta name="viewport" content="width=device-width, initial-scale=1" />
     <meta name="format-detection" content="telephone=no,address=no,email=no,date=no,url=no" />
     <title>email-message-plain</title>

     <!--[if mso]>
    <noscript>
      <xml>
        <o:OfficeDocumentSettings>
          <o:PixelsPerInch>96</o:PixelsPerInch>
          <o:AllowPNG/>
        </o:OfficeDocumentSettings>
      </xml>
    </noscript>
  <![endif]-->

     <style>
         /* --- Email client resets --- */
         html,
         body {
             margin: 0 !important;
             padding: 0 !important;
             width: 100% !important;
             height: 100% !important;
         }

         * {
             -ms-text-size-adjust: 100%;
             -webkit-text-size-adjust: 100%;
         }

         table,
         td {
             border-collapse: collapse !important;
             mso-table-lspace: 0pt;
             mso-table-rspace: 0pt;
         }

         img {
             border: 0;
             outline: none;
             text-decoration: none;
             -ms-interpolation-mode: bicubic;
             display: block;
         }

         a {
             text-decoration: none;
         }

         a[x-apple-data-detectors],
         .unstyle-auto-detected-links a,
         .aBn {
             color: inherit !important;
             text-decoration: none !important;
         }

         /* --- Typography --- */
         .text {
             font-family: Arial, Helvetica, sans-serif;
             font-size: 16px;
             line-height: 24px;
             color: #111827;
         }

         .muted {
             color: #6B7280;
             font-size: 12px;
             line-height: 18px;
         }

         /* --- Layout --- */
         .container {
             width: 550px;
             max-width: 550px;
         }

         .px {
             padding-left: 18px;
             padding-right: 18px;
         }

         .py {
             padding-top: 16px;
             padding-bottom: 16px;
         }

         /* Link styles */
         a.link {
             color: #28aae2;
         }

         a.link:hover {
             color: #2085b1;
             text-decoration: underline;
         }

         /* Mobile */
         @media screen and (max-width: 600px) {
             .container {
                 width: 100% !important;
                 max-width: 100% !important;
             }

             .px {
                 padding-left: 16px !important;
                 padding-right: 16px !important;
             }

             .img-fluid {
                 width: 100% !important;
                 height: auto !important;
             }

             .center-mobile {
                 text-align: center !important;
             }
         }
     </style>
 </head>

 <body style="margin:0; padding:0; background:#ffffff; word-break:break-word;">
     <!-- Preheader (skriven) -->
     <div
         style="display:none; font-size:1px; line-height:1px; max-height:0; max-width:0; opacity:0; overflow:hidden; mso-hide:all;">
         Poruka je spremna za čitanje.
     </div>

     <!-- Full wrapper -->
     <table role="presentation" width="100%" cellspacing="0" cellpadding="0" border="0"
         style="background:#ffffff;">
         <tr>
             <td align="center" style="padding:10px 0;">
                 <!-- Container -->
                 <table role="presentation" class="container" width="550" cellspacing="0" cellpadding="0"
                     border="0" style="width:550px; max-width:550px;">

                     <!-- “Card” surface (optional): uvek bezbedno -->
                     <tr>
                         <td style="background:#ffffff;">

                             <!-- Min height wrapper (best effort) -->
                             <div style="min-height:412px;">
                                 <!-- Header image -->
                                 <table role="presentation" width="100%" cellspacing="0" cellpadding="0"
                                     border="0">
                                     <tr>
                                         <td class="px" style="padding-top:15px;">
                                             <img class="img-fluid" alt="" width="514"
                                                 style="width:100%; max-width:514px; height:auto;"
                                                 src="data:image/png;base64," />
                                         </td>
                                     </tr>
                                 </table>

                                 <!-- Message -->
                                 <table role="presentation" width="100%" cellspacing="0" cellpadding="0"
                                     border="0">
                                     <tr>
                                         <td class="px py text" style="padding:16px 18px;">
                                             @isset($data['message'])
                                                 <!-- Safe: preserves new lines + escapes HTML -->
                                                 <div class="text"
                                                     style="font-family:Arial,Helvetica,sans-serif; font-size:16px; line-height:24px; color:#111827;">
                                                     {!! nl2br(e($data['message'])) !!}
                                                 </div>
                                             @endisset
                                         </td>
                                     </tr>
                                 </table>

                                 <!-- Spacer fallback (pomaže da “drži visinu” u klijentima koji ignorišu min-height) -->
                                 <table role="presentation" width="100%" cellspacing="0" cellpadding="0"
                                     border="0">
                                     <tr>
                                         <td height="120" style="font-size:0; line-height:0;">&nbsp;</td>
                                     </tr>
                                 </table>

                                 <!-- Footer logo/image (centered) -->
                                 <table role="presentation" width="100%" cellspacing="0" cellpadding="0"
                                     border="0">
                                     <tr>
                                         <td align="center" class="px" style="padding: 0 18px 18px 18px;">
                                             <img alt="" width="191"
                                                 style="width:191px; max-width:191px; height:auto;"
                                                 src="data:image/png;base64," />
                                         </td>
                                     </tr>
                                 </table>
                             </div>
                             <!-- /min height wrapper -->
                         </td>
                     </tr>

                 </table>
                 <!-- /Container -->
             </td>
         </tr>
     </table>
 </body>

 </html>
